import React, { useState } from 'react';
import Header from './Header';
import { useNavigate } from 'react-router-dom';

function Frontoffice() {

  const [parolaDaCercare, setparolaDaCercare] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  function handleClick() {

    setAnnoScelto('2023');

  }

  const handleSubmit = async (e) => {

    e.preventDefault(); //per non ricaricare la pagina

    try {
      const response = await fetch(`http://localhost:3000/api/filterRequest?annoScelto=${annoScelto}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        //body: JSON.stringify({ annoScelto }),
      });

      const data = await response.json();

      if (response.status === 200) {

        navigate('/login');

      } else {

        setError(data.message);
      }

    } catch (error) {
      console.error('Errore durante il login:', error);
      setError('Errore del server.');
    }


  };


  return (

    <>
        <Header></Header>

        <div className="introBlog">

          <div>

            <h2>Ciao, benvenuto nel mio blog personale, spero ti piaccia</h2>
            <button type='button' onClick={handleClick}>LOGIN</button>

          </div>

          <div class="introduzioneMioBlog">
                  <p>
                      Ciao, benvenuto nel mio blog personale, mi presento: mi chiamo Matteo Kalcich, 
                      ho 18 anni e studio informatica. Una cosa che mi piace fare durante il tempo libero 
                      Ã¨ programmare per conto mio approfondendo aspetti di mio interesse.
                  </p>
          </div>

          <div className="centroPagina">

            <form onSubmit={handleSubmit} class="ricerca">

                <p id="ricerca">RICERCA</p>
                <input type="text" name="parolaDaCercare" id="parolaDaCercare" onChange={(e) => setAnnoScelto(e.target.value)} value={annoScelto}/>
                <button type="submit" name="cerca" id="cercaBtn">CERCA</button>

            </form>

          </div>
        </div>


    </>

  );
}

export default Frontoffice;
