import React, { useState, useRef } from 'react';
import Header from './Header';
import { useNavigate } from 'react-router-dom';

function Frontoffice() {ù

  const formRef = use

  const [parolaDaCercare, setParolaDaCercare] = useState('');
  const [annoScelto, setAnnoScelto] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleClick = () => {

    navigate('/login');

  }

  const TestButton = ({name, text}) => {


    return(
      <button name={name} onClick={() => console.log(name)}>
        {text}
      </button>
    )
  }

  const handleSubmit = async (e) => {

    e.preventDefault(); //per non ricaricare la pagina

    console.log('Valore form: ');

    try {
      const response = await fetch(`http://localhost:3000/api/filterRequest?parolaDaCercare=${parolaDaCercare}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await response.json();

      if (response.status === 200) {

        navigate('/login');

      } else {

        setError(data.message);
      }

    } catch (error) {
      console.error('Errore durante il login:', error);
      setError('Errore del server.');
    }


  };


  return (

    <>
        <Header></Header>

        <div className="introBlog">

          <div>

            <h2>Ciao, benvenuto nel mio blog personale, spero ti piaccia</h2>
            <button type='button' onClick={handleClick}>LOGIN</button>

          </div>

          <div className="introduzioneMioBlog">
                  <p>
                      Ciao, benvenuto nel mio blog personale, mi presento: mi chiamo Matteo Kalcich, 
                      ho 18 anni e studio informatica. Una cosa che mi piace fare durante il tempo libero 
                      è programmare per conto mio approfondendo aspetti di mio interesse.
                  </p>
          </div>

          <div className="centroPagina">

            <form onSubmit={handleSubmit} className="ricerca">

                <p id="ricerca">RICERCA</p>
                <input type="text" name="parolaDaCercare" id="parolaDaCercare" onChange={(e) => setParolaDaCercare(e.target.value)} value={parolaDaCercare}/>
                <button type="submit" name="cerca" id="cercaBtn">CERCA</button>

            </form>



            <form onSubmit={handleSubmit} className="filtraAnno">

                  <button type="submit" name="primoPost" className="allDivFiltraAnno firstLastBtn"><p>PRIMO</p></button>

                  <button type="submit" name="precedenteAnnoPost" className="allDivFiltraAnno afterFirstBeforeLastBtn"><p>Precedente</p></button>

                  <div className="allDivFiltraAnno annoCorrente">

                      <p>Anno Scelto: {annoScelto}</p>

                  </div>

                  <button type="submit" name="successivoAnnoPost" className="allDivFiltraAnno afterFirstBeforeLastBtn"><p>Successivo</p></button>

                  <button type="submit" name="ultimoPost" className="allDivFiltraAnno firstLastBtn"><p>ULTIMO</p></button>


            </form>

          </div>

        </div>


    </>

  );
}

export default Frontoffice;
